mapper.insert:
INSERT INTO glamping
SET user_id = #{userId}
    , glamp_name = #{glampName}
    , glamp_location = #{glampLocation}
    , region = #{region}
    , extra_charge = #{extraCharge}
    , glamp_intro = #{intro}
    , info_basic = #{basic}
    , info_parking = #{parking}
    , info_notice = #{notice}
    , glamp_image = #{glampingImg}

INSERT INTO room
SET glamp_id = #{glampId}
    , room_name = #{roomName}
    , room_price = #{price}
    , room_num_people = #{peopleNum}
    , room_max_people = #{peopleMax}
    , check_in_time = #{inTime}
    , check_out_time = #{outTime}

mapper.select:
SELECT glamp_id
FROM glamping
WHERE user_id = #{userId}

SELECT glamp_id
FROM glamping
WHERE glamp_location = #{location}

mapper.update:
UPDATE glamping
SET  glamp_name = #{glampName}
    , glamp_location = #{glampLocation}
    , region = #{region}
    , extra_charge = #{extraCharge}
    , glamp_intro = #{intro}
    , info_basic = #{basic}
    , info_parking = #{parking}
    , info_notice = #{notice}
WHERE glamp_id = #{glampId}
AND user_id = #{userId}

UPDATE room
SET glamp_id = #{glampId}
    , room_name = #{roomName}
    , room_price = #{price}
    , room_num_people = #{peopleNum}
    , room_max_people = #{peopleMax}
    , check_in_time = #{inTime}
    , check_out_time = #{outTime}

mapper.select:
SELECT service_id
FROM room_service
WHERE room_id = #{roomId}
